import { Component } from '@angular/core';
import type { Meta, StoryObj } from '@storybook/angular';

import { Tabs } from './tabs';
import { TabList } from './tab-list';
import { Tab } from './tab';
import { TabPanel } from './tab-panel';
import { TabPanels } from './tab-panels';

@Component({
  standalone: true,
  imports: [Tabs, TabList, Tab, TabPanels, TabPanel],
  template: `
    <gv-tabs [(value)]="value" [variant]="variant">
      <gv-tab-list>
        <gv-tab value="overview">
          <span class="gv-tab__label">
            <span class="gv-tab__icon">üè†</span>
            Overview
            <span class="gv-tab__badge">3</span>
          </span>
        </gv-tab>

        <gv-tab value="billing">
          <span class="gv-tab__label">
            <span class="gv-tab__icon">üí≥</span>
            Billing
          </span>
        </gv-tab>

        <gv-tab value="audit" [disabled]="true">
          <span class="gv-tab__label">
            <span class="gv-tab__icon">üßæ</span>
            Audit
          </span>
        </gv-tab>

        <gv-tab value="settings">
          <span class="gv-tab__label">
            <span class="gv-tab__icon">‚öôÔ∏è</span>
            Settings
            <span class="gv-tab__badge">12</span>
          </span>
        </gv-tab>
      </gv-tab-list>

      <gv-tab-panels>
        <gv-tab-panel value="overview">Overview content</gv-tab-panel>
        <gv-tab-panel value="billing">Billing content</gv-tab-panel>
        <gv-tab-panel value="audit">Audit content</gv-tab-panel>
        <gv-tab-panel value="settings">Settings content</gv-tab-panel>
      </gv-tab-panels>
    </gv-tabs>

    <div style="margin-top: 12px; font-size: 12px; opacity: 0.8;">
      Active tab (debug): <strong>{{ value }}</strong>
    </div>
  `,
})
class TabsStoryHost {
  value = 'overview';
  variant: 'underline' | 'pill' = 'underline';
}

const meta: Meta<TabsStoryHost> = {
  title: 'Gravitas/Tabs',
  component: TabsStoryHost,
  argTypes: {
    variant: { control: 'select', options: ['underline', 'pill'] },
  },
  args: {
    variant: 'underline',
  },
};

export default meta;

type Story = StoryObj<TabsStoryHost>;

export const Default: Story = {
  args: { variant: 'underline' },
};

import type { Meta, StoryObj } from '@storybook/angular';
import { Component, Input, inject } from '@angular/core';
import { Toasts } from './toasts';
import { ToastService } from './toasts.service';
import { ToastPosition } from './toast.model';
import { Button } from '../button/button';

@Component({
  selector: 'gv-toast-demo',
  standalone: true,
  imports: [Toasts, Button],
  template: `
    <div class="p-3 d-flex gap-2">
      <gv-button variant="success" (click)="success()">Success</gv-button>
      <gv-button variant="danger" (click)="error()">Error (sticky)</gv-button>
      <gv-button variant="info" (click)="info()">Info</gv-button>
    </div>

    <gv-toasts></gv-toasts>
  `,
})
class ToastDemo {
  private toast = inject(ToastService);

  @Input() position: ToastPosition = 'top-end';

  success() {
    this.toast.show({
      variant: 'success',
      title: 'Saved',
      message: 'Claim updated successfully.',
      position: this.position,
    });
  }

  error() {
    this.toast.show({
      variant: 'danger',
      title: 'Validation error',
      message: 'Password must be at least 8 characters long.',
      position: this.position,
      durationMs: 0,
      ariaLive: 'assertive',
    });
  }

  info() {
    this.toast.show({
      variant: 'info',
      title: 'Sync',
      message: 'Posting to EDI gateway‚Ä¶',
      position: this.position,
    });
  }
}

const meta: Meta<ToastDemo> = {
  title: 'Gravitas/Toast',
  component: ToastDemo,
  args: { position: 'top-end' },
  argTypes: {
    position: {
      control: 'select',
      options: [
        'top-end',
        'top-start',
        'top-center',
        'bottom-end',
        'bottom-start',
        'bottom-center',
      ],
    },
  },
  decorators: [
    (story) => ({
      ...story(),
      moduleMetadata: {
        providers: [ToastService], // ‚úÖ single instance shared by demo + <gv-toasts>
      },
    }),
  ],
};

export default meta;
type Story = StoryObj<ToastDemo>;

export const Playground: Story = {};

