      description: 'Native button type attribute.',
      table: { category: 'Behavior', defaultValue: { summary: 'button' } },
    },

    // State
    disabled: {
      control: 'boolean',
      description: 'Disables the button.',
      table: { category: 'State', defaultValue: { summary: 'false' } },
    },
    loading: {
      control: 'boolean',
