  decorators: [
    (storyFn: any, context: any) => {
      applyGravitasGlobals(context.globals);

      // ‚úÖ In Docs, do NOT wrap in the shell at all.
      if (context?.viewMode === 'docs') {
        return storyFn();
      }

      // ‚úÖ In Canvas (story view), wrap in the Gravitas shell.
      const story = storyFn();

      return {
        ...story,
        moduleMetadata: {
          ...(story.moduleMetadata ?? {}),
          imports: [
            ...((story.moduleMetadata?.imports as any[]) ?? []),
            ShellWrapperComponent,
          ],
        },
        template: `
          <gv-story-shell>
            ${story.template ?? ''}
          </gv-story-shell>
        `,
        props: story.props ?? {},
      };
    },
  ],
}

